import React, { useState, useReducer } from 'react';
import { Card, Avatar } from 'antd';
import Link from 'next/link';
import { useEffect } from 'react';
import { getMovies } from '../actions';
import { initialState, reducer } from '../reducer';
import { GET_MOVIE_REQUEST, GET_MOVIE, GET_MOVIE_FAILURE } from '../constant';

export default function Index() {
    const [movie, setMovie] = useState('spiderman');
    const [state, dispatch] = useReducer(reducer, initialState);
    console.log(state);
    const { Meta } = Card;

    async function getMoviesData() {
        dispatch({
            type: GET_MOVIE_REQUEST,
        });
        try {
            const response = await (await getMovies({ movie })).json();
            dispatch({
                type: GET_MOVIE,
                payload: response,
            });
        } catch (error) {
            dispatch({
                GET_MOVIE_FAILURE,
            });
        }
    }

    useEffect(() => {
        getMoviesData();
    }, []);
    return (
        <Link href="/moviedetail">
            <Card
                style={{ width: 300 }}
                cover={
                    <img
                        alt="example"
                        src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png"
                    />
                }
            >
                <Meta
                    // avatar={
                    //     <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                    // }
                    title="Bat man"
                    description="This is the batman"
                />
            </Card>
        </Link>
    );
}
